import react,{useState} from "react";
import { useLocation, useParams } from "react-router-dom";
import './productDetails.css'

function ProductDetails(props)
{
  const [productDetail, setProductDetail] = useState([]);
let productId = useParams()
  console.log(productId)

  fetch('https://ecommerce370000.herokuapp.com/productid',{
  method: "POST",
  body: JSON.stringify(productId),
  headers: {
    "Content-type": "application/json; charset=UTF-8",
  },
  credentials: "include",
})
.then(res => res.json())
.then(
  (result) => {
    // console.log(result)
    // props.setCartCount(result)
    // cartCount = result;
    setProductDetail()
  }
) .catch((err) => {
  console.log(err);
})

 return (
<>
<div className="container">
<div class="con">
    <div class="box one">
      <div class="details">
        <div class="topic">Description</div>
        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Cumque consequatur corporis vitae nobis, ut veniam earum expedita eaque at placeat perferendis unde voluptates explicabo rerum distinctio quis, illo, porro et?</p>
        <div class="rating">
          <i class="fas fa-star"></i>
          <i class="fas fa-star"></i>
          <i class="fas fa-star"></i>
          <i class="fas fa-star"></i>
          <i class="fas fa-star"></i>
          <i class="fas fa-star"></i>
          <i class="fas fa-star"></i>
          <i class="fas fa-star"></i>
          <i class="far fa-star"></i>
          <i class="far fa-star"></i>
        </div>
          <div class="price-box">
            <div class="discount">$1800.00</div>
            <div class="price">$1500.00</div>
          </div>
      </div>
      <div class="button1">
        <button>Add To Cart</button>
      </div>
    </div>
    <div class="box two">
      <div class="image-box">
        <div class="image">
          <img src="images/camera.png" alt=""/>
        </div>
        <div class="info">
          <div class="brand">SONY</div>
          <div class="name">SONY ALPHA A7 KIT</div>
          <div class="shipping">FREE SHIPPING</div>
          <div class="button2">
            <button>Login For More</button>
          </div>
        </div>
      </div>
    </div>
  </div>
  </div>
</>
 )   
}

export default ProductDetails;
