import React ,{useState,useEffect} from "react"
import '../App.css';
import Item from "./item";
import HeaderHome from "./Partials/HeaderHome";
import './Home.css'


function Home()
{
 
 const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  useEffect(()=>{
   getProducts();
  },[]);

  const [startFrom , setStartFrom] = useState(5);
  var limit = 5;
  const [children, setChildren] = useState([]);

  function load(e){
    // Incrementing the offset so you can get next records when that button is clicked
    e.preventDefault();
    setStartFrom(startFrom + limit);
    var data = {
      text: startFrom
    }
    // console.log(data.text)
    fetch('http://localhost:3001/product', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: {
        'Content-type': 'application/json; charset=UTF-8',
      },
    })
       .then((response) => response.json())
       .then((data) => {
          // console.log(data);
          // {data.map((todo,index) => (
          //   <Item name={todo.name} thumbnail={todo.thumbnail} _id={todo._id} category={todo.category} price={todo.price} rating = {todo.rating} key={index} />
          // ))}
          // console.log(1);

          
            // children.push(data)
            //   let newArray=children.map(function(item){
            //     return item;
            //   })
            //   // console.log(newArray);
              // setChildren(newArray)

            setChildren([...children,data])
            
            
          

          // Handle data
       })
       .catch((err) => {
          console.log(err.message);
       });
  }


 function getProducts(){

    fetch("http://localhost:3001/product")
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        // console.log(result)
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )


  return;
}

function loginOrNot(){
  if(session.loggedIn)
  return <a>Logout</a>
}

  if (error) {
    return <div><h1>Server is down</h1></div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {

    return (
      <>

        <HeaderHome/>

      <form onSubmit={load}>
       {items.map((todo,index) => (
         <Item name={todo.name} thumbnail={todo.thumbnail} _id={todo._id} category={todo.category} price={todo.price} rating = {todo.rating} key={index} />
         ))}

       	{children.map((t, i)=>{
           return t.map((todo,index)=>{
             return <Item name={todo.name} thumbnail={todo.thumbnail} _id={todo._id} category={todo.category} price={todo.price} rating = {todo.rating} key={index} />
            })
          })}
          <button type="submit">Load More</button>
          </form>
      </>
    );
  }}

export default Home;
