import React ,{useState,useEffect} from "react"
import '../App.css';


function Home()
{
 
 const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  useEffect(()=>{
   getProducts();
  },[]);

 function getProducts(){

    fetch("http://localhost:3001/product")
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        console.log(result)
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )


  return;
}


  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <ul>
       {items.map(todo => (
          <div class="column" key={todo.id}>
          <div class="card" id="todoImg">
        <img id="tImg" src="https://th.bing.com/th/id/OIP.V_dTwMOEosfIPpdpp96lwwHaHa?pid=ImgDet&rs=1" alt="Avatar" />
        <div class="container">
          <h4><b>{todo.name}</b></h4> 
          <button id= {todo._id}  onclick="getId(this.id)"> View Details </button>
          <button class ={todo._id} onclick="getClass(this.getAttribute('class'))"> Add Cart </button>
      
        </div>
      
      </div>
      </div>
        ))}
      </ul>
    );
  }}

export default Home;
