import React,{useEffect} from "react";
import './Cart.css'

function Cart(props){

    useEffect(()=>{
        if(props.sess.user)
        {
        var data = {email:props.sess.user.email}
        // var body = {"email": props.sess.user.email}
      fetch('http://localhost:3001/cartData',{
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-type": "application/json; charset=UTF-8",
        },
        credentials: "include",
      })
      .then(res => res.json())
      .then(
        (result) => {
          // console.log(result)
          // cartCount = result;
          console.log(result)
        }
      ) .catch((err) => {
        console.log(err);
      })}
      },[])

return (
    <>
    
    <div class="container">

	<h1>Shopping Cart</h1>

	<div class="cart">

		<div class="products">

			<div class="product">

				<img src="https://th.bing.com/th/id/OIP.nuEfzOXLoJ1XPEKe69oOkwHaHa?pid=ImgDet&rs=1"/>

				<div class="product-info">

					<h3 class="product-name">New Shoes</h3>

					<h4 class="product-price">₹ 1,000</h4>

					<h4 class="product-offer">50%</h4>

					<div class="product-quantity">Qnt: <input value="1" name=""/>

					<p class="product-remove">

						<i class="fa fa-trash" aria-hidden="true"></i>

						<span class="remove">Remove</span>

					</p>

				</div>

			</div>

			<div class="product">

				<img src="https://th.bing.com/th/id/OIP.nuEfzOXLoJ1XPEKe69oOkwHaHa?pid=ImgDet&rs=1"/>

				<div class="product-info">

					<h3 class="product-name">New Bag</h3>

					<h4 class="product-price">₹ 2,000</h4>

					<h4 class="product-offer">40%</h4>

					<div class="product-quantity">Qnt: <input value="1" name=""/>

					<p class="product-remove">

						<i class="fa fa-trash" aria-hidden="true"></i>

						<span class="remove">Remove</span>

					</p>

				</div>

			</div>

		</div>

		<div class="cart-total">

			<p>

				<span>Total Price</span>

				<span>₹ 3,000</span>

			</p>

			<p>

				<span>Number of Items</span>

				<span>2</span>

			</p>

			<p>

				<span>You Save</span>

				<span>₹ 1,000</span>

			</p>

			<a href="#">Proceed to Checkout</a>

		</div>

	</div>

</div>
</div>
</div>
    </>
)
}

export default Cart;
