import React ,{useState,useEffect} from "react"
import '../App.css';
import Item from "./item";
import HeaderHome from "./Partials/HeaderHome";
import './Home.css'


function Home()
{
 
 const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  useEffect(()=>{
   getProducts();
  },[]);

  function load(){
    
  }


 function getProducts(){

    fetch("http://localhost:3001/product")
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        console.log(result)
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )


  return;
}


  if (error) {
    return <div><h1>Server is down</h1></div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <>

        <HeaderHome/>
      
       {items.map((todo,index) => (
          <Item name={todo.name} thumbnail={todo.thumbnail} _id={todo._id} category={todo.category} price={todo.price} rating = {todo.rating} key={index} />
        ))}

        <button onClick= {load}>Load More</button>
      </>
    );
  }}

export default Home;
