import React, { useEffect, useState } from "react";
import HeaderHome from "../Partials/HeaderHome";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

import "./Cart.css";
function Cart(props) {
  const [values, setValues] = useState([]);

  useEffect(() => {
    console.log(1);
    if (props.sess.user) {
      // console.log(props.sess.user)
      var data = { email: props.sess.user.email };
      // var body = {"email": props.sess.user.email}
      fetch("http://localhost:3001/cartData", {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-type": "application/json; charset=UTF-8",
        },
        credentials: "include",
      })
        .then((res) => res.json())
        .then((result) => {
          // console.log(result)
          // cartCount = result;
          setValues(result);
        })
        .catch((err) => {
          console.log(err);
        });
    }
  }, [props]);

  // console.log(values)
  // var valueArr = [];

  //    var quantId = counting(values)
  //   console.log(quantId)

  //       function counting(arr)
  // {
  const count = [
    ...values.reduce((map, obj) => map.set(obj._id, obj), new Map()).values(),
  ];

  // console.log(count[1]);

  // for(const c of count)
  // {
  //   console.log(c)
  // }
  // return count;
  // var countVal = [];

  // console.log(values[0]._id)
  // console.log(props._id)
const [items, setItems] = useState([]);

  function getProducts(){

    fetch("http://localhost:3001/product")
      .then(res => res.json())
      .then(
        (result) => {
          setItems(result);
        // console.log(result)
        },
        (error) => {
          setError(error);
        }
      )


  return;
}

 function addCart() {
    props.setCartCount(props.cartCount+1)
    console.log(props.sess.user.email);
    var body = {
      "email": props.sess.user.email,
      "cartId": props._id
    }
    fetch("http://localhost:3001/cart", {
      method: "POST",
      body: JSON.stringify(body),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
      credentials: "include",
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(1);
        // window.location.href = "/";
      })
      .catch((err) => {
        console.log(err);
      });
  }


  var quantity = (id) => {
    let num = 0;
    for (let i = 0; i < values.length; i++) {
      if (values[i]._id == id) num++;
    }
    return num;
  };

  // console.log(props.cartCount)
  // var quantity = 2

  // }
  // if(props.sess.isLoggedIn){

  if (props.sess.isLoggedIn == false) {
    window.location.href = "/login";
  }
  // console.log(values[0]);
  else
    return (
      <>
        <HeaderHome
          sess={props.sess}
          cartCount={props.cartCount}
          setCartCount={props.setCartCount}
        />

        <div className="cart transition is-open">
          {/* <Routes> */}
          <Link to="/" className="btn btn-update" id="update">
            Continue Shopping
          </Link>
          {/* </Routes> */}

          <div className="table">
            <div className="layout-inline row th">
              <div className="col col-pro">Product</div>
              <div className="col col-price align-center ">Price</div>
              <div className="col col-qty align-center">QTY</div>
              <div className="col">VAT</div>
              <div className="col">Total</div>
            </div>

            {count.map((t, i) => (
              <div className="layout-inline row">
                <div className="col col-pro layout-inline">
                  <img
                    src="https://cdn.sweatband.com/mizuno_wave_inspire_14_ladies_running_shoes_aw18_mizuno_wave_inspire_14_ladies_running_shoes_aw18-_angled_2000x2000.jpg"
                    alt="kitten"
                    id="productImg"
                  />
                  <p>{t.name}</p>
                </div>

                <div className="col col-price col-numeric align-center ">
                  <p>₹{t.price}</p>
                </div>

                <div className="col col-qty layout-inline">
                  <btn className="qty qty-minus">-</btn>
                  <input
                    type="numeric"
                    value={quantity(t._id)}
                    id="quantityVal"
                  />
                  <btn className="qty qty-plus" onClick={addCart}>+</btn>
                </div>

                <div className="col col-vat col-numeric">
                  <button>Delete</button>
                </div>
                <div className="col col-total col-numeric">
                  {" "}
                  <p> {t.price * quantity(t._id)}</p>
                </div>
              </div>
            ))}

            <div className="tf">
              <div className="row layout-inline">
                <div className="col">
                  <p>VAT</p>
                </div>
                <div className="col"></div>
              </div>
              <div className="row layout-inline">
                <div className="col">
                  <p>Shipping</p>
                </div>
                <div className="col"></div>
              </div>
              <div className="row layout-inline">
                <div className="col">
                  <p>Total</p>
                </div>
                <div className="col"></div>
              </div>
            </div>
          </div>

          <a href="#" className="btn btn-update" id="update">
            Update cart
          </a>
        </div>
      </>
    );
  // }
  // else{
  // window.location.href('/login')
  // }
}

export default Cart;
