import React, { useState, useEffect } from "react";
import "./HeaderHome.css";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

function HeaderHome() {
  const [session, setSession] = useState(false);

  useEffect(() => {
    fetch("http://localhost:3001/login", { credentials: "include" })
      // .then(res => res.json())
      .then((response) => response.json())
      .then((data) => {
        if (data.loggedIn === true) setSession(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

var logout;
fetch('http://localhost:3001/signup', {
			method: 'POST',
			body: JSON.stringify(session),
			headers: {
			  'Content-type': 'application/json; charset=UTF-8',
			},
		  })
			 .then((response) => response.json())
			 .then((data) => {
		window.location.href='/';
				
								 })
			 .catch((err) => {
				console.log(err);
			 });
	

  var loginOrNot;
  if (session.loggedIn)
    loginOrNot = <a>{logout}</a>;
  else loginOrNot = <Link to="/login">Login</Link>;



  return (
    <div className="head">
      <i id="carti" class="fa fa-shopping-cart"></i>
      <h2 id="user">
        Welcome {session.loggedIn === true ? session.user.firstname : "Guest"}
      </h2>
      
      <h3 id="userLogin">{loginOrNot}</h3>
      <span id="header">
        {/* <h2 id="user"> {session.loggedIn===true?"logout":"login"}</h2> */}
        <h1>MyShop</h1>
        {/* <img src="<%= user.profile_pic %>"/> */}
        {/* <!-- <img src="/p"/> --> */}
        <link
          rel="stylesheet"
          href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"
        />

        {/* <a href="/logout">logout</a> */}

        <br></br>
      </span>
    </div>
  );
}

export default HeaderHome;
